mapper.select:
select * from media_data where textType is null
and writeDate > #{startDate}
and writeDate < #{endDate}
order by createDate desc limit #{startPage}, #{perPageNum}

select count(*) from media_data where textType is null
and writeDate > #{startDate}
and writeDate < #{endDate}

mapper.update:
	update
		media_data
	set
		textType = #{textType}
	where
		media_idx = #{media_idx}

	update
		media_data
	set
		thumbnail = #{thumbnail}
	where
		media_idx = #{media_idx}
