mapper.select:
select * from community_data where textType is null
and writeDate > #{startDate}
and writeDate < #{endDate}
order by createDate desc limit #{startPage}, #{perPageNum}

select count(*) from community_data where textType is null
and writeDate > #{startDate}
and writeDate < #{endDate}

mapper.update:
	update
		community_data
	set
		textType = #{textType}
	where
		community_idx = #{community_idx}

	update
		community_data
	set
		thumbnail = #{thumbnail}
	where
		community_idx = #{community_idx}
