mapper.select:
select count(if(textType='좋은글', textType, null)) lik, count(if(textType='나쁜글', textType, null)) dis, count(if(textType='관심글', textType, null)) cu, count(if(textType='기타글', textType, null)) etc, count(if(textType is not null and texttype!='삭제글', textType, null)) al from union.media_data where textType is not null
and keyword = 'press'
and left(writeDate,10) >= #{startDate} and left(writeDate,10) <= #{endDate}

select count(*) from media_data where textType is not null and textType!='삭제글'
and writeDate >= CURDATE()
and writeDate > #{startDate} and writeDate < #{endDate}

 		select 
 			count(*) 
 		from 
 			media_data
 		where
 			media_name = #{keyword}
 		and
			textType!='삭제글'
 		
			and updateDate > #{startDate}
			and updateDate < #{endDate}

 		select 
 			count(*) 
 		from 
 			media_data
 		where
 			reporter_name = #{keyword}
 		
			and updateDate > #{startDate}
			and updateDate < #{endDate}

 		select 
 			writeDate, media_name, media_title, keyword, textType
 		from 
 			media_data
 		where
 			reporter_name = #{reporter}
 		and
 			textType = #{textType}	
 		
			and updateDate > #{startDate}
			and updateDate < #{endDate}

mapper.update:
	update
		media_data
	set
		thumbnail = #{thumbnail}
	where
		media_idx = #{media_idx}
