mapper.select:
	select count(*)
	from portal_data
	where createDate between date_add(#{date}, interval -1 hour) and #{date}

select * from portal_data where textType is null
and writeDate > #{startDate}
and writeDate < #{endDate}
order by createDate desc limit #{startPage}, #{perPageNum}

select count(*) from portal_data where textType is null
and writeDate > #{startDate}
and writeDate < #{endDate}

mapper.update:
	update
		portal_data
	set
		thumbnail = #{thumbnail}
	where
		portal_idx = #{portal_idx}
